long long sq(long long x)
{
 long long low = 0, high = 1000000000;
 while(low < high)
 {
  long long mid = (low + high + 1) / 2;
  if(mid * mid > x)
   high = mid - 1;
  else
   low = mid;
 }
 return low;
}

int main()
{
 ios::sync_with_stdio(0);
 cin >> Q;

 for(long long i = 2; i <= 1000000; i++)
 {
  long long x = i * i;
  while(x <= INFTY/i)
  {
   x *= i;
   long long l = sq(x);
   if(l * l != x)
    p.insert(x);
  }
 } 

 vector<long long> v(p.begin(), p.end());

 for(int a = 0; a < Q; a++)
 {
  long long L, R;
  cin >> L >> R;

  long long ans = 0;
  ans += upper_bound(v.begin(), v.end(), R) - lower_bound(v.begin(), v.end(), L);
  ans += sq(R) - sq(L - 1);
  cout << ans << "\n";
 }

 return 0;
}

