                if(v[i].first == v[j].first && knows[v[i].second][v[j].second] == false)
                    continue;
                if(v[i].first == v[j].second && knows[v[i].second][v[j].first] == false)
                    continue;
                if(v[i].second == v[j].first && knows[v[i].first][v[j].second] == false)
                    continue;
                if(v[i].second == v[j].second && knows[v[i].first][v[j].first] == false)
                    continue;
                int result_here = 0;
                result_here += n_knows[v[i].first] + n_knows[v[i].second] + n_knows[v[j].first] + n_knows[v[j].second] - 6;
                if(v[i].first == v[j].first || v[i].first == v[j].second)
                    result_here -= n_knows[v[i].first];
                else
                    result_here -= n_knows[v[i].second];

                //printf("%d %d %d %d %d\n", v[i].first, v[i].second, v[j].first, v[j].second, result_here);
                result = min(result, result_here);
            }

        }
    }

    if(result==INF)
        printf("-1");
    else
        printf("%d", result );

    return 0;
}

