 for (int i = 0; i < s.size(); i++)
 {
  if(s[i] == '*') s[i] = ' ';
 }

 ss.clear();
 ss << s;
 int ans = 0;
 while (ss >> s)
 {
  if((int)s.size() < k) continue;
  ans += (int)s.size()-k+1;
 }
 return ans;
}

