#include<iostream>
#include<vector>
using namespace std;

struct node{
 bool draw,odd,even,visited,recstack;
};

vector<int> vec[100007];
node graph[100007];

void dfs(int ind)
{
 graph[ind].visited = true;
 graph[ind].recstack = true;
 int i,j,k,ln = vec[ind].size();
 for(i = 0;i<ln;i++)
 {
  j = vec[ind][i];
  //cout<<j<<"--\n";
  if(graph[j].visited == false)
  {
   //cout<<j<<"\n";
   dfs(j);
   if(graph[j].draw == true)
   {
    graph[ind].draw = true;
   }
   if(graph[j].even == true)
   {
    graph[ind].odd = true;
   }
   if(graph[j].odd == true)
   {
    graph[ind].even = true;
   }
  }
  else if(graph[j].recstack == false)
  {
   //cout<<j<<"\n";
   if(graph[j].draw == true)
   {
    graph[ind].draw = true;
   }
   if(graph[j].even == true)
   {
    graph[ind].odd = true;
   }
   if(graph[j].odd == true)
   {
    graph[ind].even = true;
   }
  }
  else
  {
   graph[ind].draw = true;
  }
 }
 if(ln == 0)
 {
  graph[ind].even = true;
 }
 //cout<<ind<<"Here\n";
 graph[ind].recstack = false;
}

void printVertices(int ind,bool o)
{
 cout<<ind<<" ";
 graph[ind].visited = true;
 graph[ind].recstack = true;
 int i,j,k,ln = vec[ind].size();
 for(i = 0;i<ln;i++)
 {
  j = vec[ind][i];
  if(!graph[j].visited)
  {
   if(o&&graph[j].even)
   {
    printVertices(j,false);
    return;
   }
   else if((o == false)&&(graph[j].odd))
   {
    printVertices(j,true);
    return;
   }
  }
 }
}

int  main()
{
 int i,j,k,n,m,c,s;
 cin>>n>>m;
 for(i = 1;i<=n;i++)
 {
  cin>>k;
  for(j = 1;j<=k;j++)
  {
   cin>>c;
   vec[i].push_back(c);
  }
 }
 cin>>s;
 for(i = 1;i<=n;i++)
 {
  graph[i].visited = false;
  graph[i].odd = false;
  graph[i].even = false;
  graph[i].draw = false;
  graph[i].recstack = false;
 }
 dfs(s);
 cout<<s<<"\n";
 if(graph[s].odd == true)
 {
  cout<<"Win\n";
  for(i = 1;i<=n;i++)
  {
   graph[i].visited = false;
   graph[i].recstack = false;
  }
  printVertices(s,true);
 }
 else if(graph[s].draw)
 {
  cout<<"Draw";
 }
 else
 {
  cout<<"Lose";
 }
}

