#include<bits/stdc++.h>


using namespace std;


typedef long long ll;
typedef pair<int, int> pi;
typedef tuple<int, int, int> ti;










void solve() {
    int n, m;
    cin >> n >> m;


    stack<int> st;
    vector<bool> instack(m+1);
    vector<int> tin(m+1), low(m+1);
    vector<int> notlike(m+1);
    vector<vector<int>> g(m+1);
    int timer=1;

    vector<pi> data(n);

    for(int i=0; i<n; i++) {
        cin >> data[i].first >> data[i].second;
    }

    for(int i=0; i<m/2; i++) {
        int a, b;
        cin >> a >> b;
        notlike[a]=b;
        notlike[b]=a;
    }

    for(int i=0; i<n; i++) {
        auto [a, b]=data[i];
        g[a].push_back(notlike[b]);
        g[b].push_back(notlike[a]);
    }


    function<void(int)> dfs=[&](int curr) {
        tin[curr]=low[curr]=timer++;
        instack[curr]=true;
        st.push(curr);

        for(auto &next: g[curr]) {
            if(!low[next]) dfs(next);
            if(instack[next]) low[curr]=min(low[curr], low[next]);
        }

        if(tin[curr]==low[curr]) {
            while(st.size()) {
                int node=st.top(); st.pop();
                low[node]=low[curr];
                instack[node]=false;
                if(node==curr) break;
            }
        }
    };

    for(int i=1; i<=m; i++) {
        if(!low[i]) dfs(i);
    }

    bool valid=true;
    for(int i=1; i<=m; i++) {
        if(low[i]==low[notlike[i]]) {
            valid=false;
            break;
        }
    }

    cout << (valid ? 'Y' : 'N');
}


int main() {
    ios::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);

    int q=5;

    while(q--) solve();

    return 0;
}

