struct VISUKITUPIE{
 pair<int, int> t[1 << 24];
 int mask;
 VISUKITUPIE() { mask = (1 << 24); for (int i = 0; i < (1 << 24); i++) t[i] = mp(-1, 0); }

 void up(int v, int val){
  int save = v;
  v = (v * (ll)v + 3 * v + 7) & (mask - 1);
  while(1){
   if (t[v].x == -1){
    t[v].x = save;
    t[v].y = val;
    return;
   }
   if (t[v].x == save){
    t[v].y += val;
    return;
   }
   v++;
   if (v == mask) v = 0;
  }
 }

 int get(int v){
  int save = v;
  v = (v * (ll)v + 3 * v + 7) & (mask - 1);
  while(1){
