double max(qd x, qd y)
{
    if( x - y >= eps)
        return x;    
    return y;        
}

qd abss(qd x)
{
    return x > 0 ? x : -x;    
}

qd min(qd x, qd y)
{
   // return x < y ? x : y;    
    if(x - y <= -eps)
        return x;
    return y;
}

qd f(qd x)
{
    for(int i = 0; i < n; i++)
        b[i] = a[i] - x;      
    ma = b[0];
    d[0] = b[0];
    for(int i = 1; i < n; i++)
    {
     //   ma = max(ma, max(b[i] + b[i])    
        d[i] = max(b[i], b[i] + d[i - 1]);
        ma = max(ma, d[i]);
    }  
    mi = b[0];
    for(int i = 1; i < n; i++)
    {
        d[i] = min(b[i], b[i] + d[i - 1]);    
        mi = min(mi, d[i]);
    }
    return abss(ma) - abss(mi);  
}

