using namespace std;
#define mst(a,b) memset((a),(b),sizeof(a))
#define rush() int T;scanf("%d",&T);while(T--)
#define lson l,mid,rt<<1
#define rson mid+1,r,rt<<1|1

typedef long long ll;
const int maxn = 300005;
const ll mod = 1e9+7;
const int INF = 0x3f3f3f3f;
const double eps = 1e-9;

int n,m;
int d[maxn];
int a[maxn];
int tree[maxn<<2];
int Max[maxn<<2];

void init()
{
    mst(d,0);
    for(int i=1;i<maxn;i++)
    for(int j=i;j<maxn;j+=i)
    {
        d[j]++;
    }
}

void pushup(int rt)
{
    tree[rt]=tree[rt<<1|1]+tree[rt<<1];
    Max[rt]=max(Max[rt<<1|1],Max[rt<<1]);
}

void build(int l,int r,int rt)
{
    if(l==r)
    {
        tree[rt]=Max[rt]=a[l];
        return;
    }
    int mid=(l+r)>>1;
    build(lson);
    build(rson);
    pushup(rt);
}

void update(int x,int y,int l,int r,int rt)
{
    if(Max[rt]<=2) return;
    if(l==r)
    {
        tree[rt]=Max[rt]=d[tree[rt]];
        return;
    }
    int mid=(l+r)>>1;
    if(x<=mid) update(x,y,lson);
    if(y>mid) update(x,y,rson);
    pushup(rt);
}

int query(int x,int y,int l,int r,int rt)
{
    if(x<=l&&r<=y) return tree[rt];
    int mid=(l+r)>>1;
    int ans=0;
    if(x<=mid) ans+=query(x,y,lson);
    if(y>mid) ans+=query(x,y,rson);
    return ans;
}

int main()
{
    init();
    scanf("%d%d",&n,&m);
    for(int i=1;i<=n;i++)
    {
        scanf("%d",&a[i]);
    }
    build(1,n,1);
    int op,l,r;
    for(int i=1;i<=m;i++)
    {
        scanf("%d%d%d",&op,&l,&r);
        if(op==1) update(l,r,1,n,1);
        else printf("%d\n",query(l,r,1,n,1));
    }
    return 0;
}

