        cin >> mat[i];
        if (start == pair<int,int>({-1, -1}) || end == pair<int,int>({-1, -1})) {
            for (int j = 0 ; j < m ; j++) {
                if (mat[i][j] == 'S') start = {i, j};
                else if (mat[i][j] == 'E') end = {i, j};
            }
        }
    }
    queue<pair<pair<int,int>, pair<int,int>>> bag;
    vector<vector<vector<bool>>> v(10, vector<vector<bool>>(n, vector<bool>(m, 0)));
    bag.push({start,{0, 0}});
    v[0][start.f][start.s] = 1;
    int di[4] = {-1, 1, 0, 0};
    int dj[4] = {0, 0, -1, 1};
    int ans = -1;
    while(!bag.empty()) {
        int i = bag.front().f.f;
        int j = bag.front().f.s;
        int cnt = bag.front().s.f;
        int state = bag.front().s.s;
        bag.pop();
        // cout << i << ' ' << j << endl;
        if (mat[i][j] == 'E') {
            ans = cnt;
            break;
        }
        if (mat[i][j] == 'b' && (state & 1) == 0) {
            state = state ^ 1;
        } else if (mat[i][j] == 'p' && (state & (1 << 1)) == 0) {
            state = state ^ (1 << 1);
        } else if (mat[i][j] == 'j' && (state & (1 << 2)) == 0) {
            // cout << "sdf" <<endl;
