double calc(double x) {
    double mx = -INF;
    double mn = INF;
    for (int i = 0; i <= n; i++) {
        double val = p[i] - x * i;
        mx = max(mx, val);
        mn = min(mn, val);
    }
    return mx - mn;
}

void solve() {
    for (int i = 0; i < n; i++)
        p[i + 1] = p[i] + a[i];
    double l = -T; 
    double r = T;
    for (int tt = 0; tt < G; tt++) {
        double m1 = (l + l + r) / 3;
        double m2 = (l + r + r) / 3;
        double r1 = calc(m1);
        double r2 = calc(m2);
        if (r1 > r2)
            l = m1;
        else
            r = m2;
    }
    //db(l);
    printf("%.17f\n", calc(l));
}

