long long v[N], d[N], p[N];
int ans[N];
long long bit[N];
bool check[N];

void update(int u, long long v)
{
    //cout << u << " " << v << endl;
    while( u < N)
    {
        bit[u] += v;
        u += ( u & (-u) );
    }
}

long long get(int u)
{
    //cout << u << endl;
    long long ans = 0;
    while( u )
    {
        ans += bit[u];
        u -= ( u & (-u) );
    }
    return ans;
}



int main()
{
    ios_base::sync_with_stdio(false);
    int n;
    cin >> n;
    for(int x=1; x<=n; ++x)
        cin >> v[x] >> d[x] >> p[x];
        int k = 0;
    for(int x=1; x<=n; ++x)
    {
        //cout << x << endl;
        if(check[x]) continue;
        check[x] = 1;
        ans[k++] = x;

        for(int i = x+1; i<n; ++i)
        {
            p[i] -= v[x] - ( i - x - 1 );
        }
        bool ok = 1;
        while(ok)
        {
            ok = 0;
            for(int i=1; i<=n; ++i)
            {
                if(check[i]) continue;
                if(get(i) > p[i]) { update(1,d[i]); check[i] = 1; ok = 1; }
                //cout << 1 << endl;
            }
        }
    }
    cout << k << endl;
    for(int x=0; x<k; ++x) cout << ans[x] << " ";
}

