#include "robot.h"

int dir(int x)
{
    if (x < 2)
        return 0;
    return (x - 2) % 4 + 1;
}

int state(int x)
{
    if (x < 2)
        return 0;
    return (x - 2) / 4 + 1;
}

int color(int dir, int state)
{
    return state * 4 + dir - 3;
}

char move[5] = {'_', 'W', 'S', 'E', 'N'};

int opp[] = {0, 3, 4, 1, 2};

void get_move(std::vector<int> S, int &Z, char &A)
{
    bool is_start = S[1] == -2 && S[4] == -2;
    bool is_end = S[2] == -2 && S[3] == -2;
    int stackdir = 0;
    bool path = is_end;
    for (int i = 1; i <= 4; i++)
    {
        if (state(S[i]) == 2 && dir(S[i]) == opp[i])
        {
            stackdir = i;
        }
        else if (S[i] == 1)
        {
            path = true;
        }
    }
    if (path)
    {
        Z = 1;
        A = stackdir ? move[stackdir] : 'T';
        return;
    }
    if (S[0] == 0)
    {
        if (stackdir)
        {
            Z = color(stackdir, 1);
            A = move[stackdir];
        }
        else if (is_start)
        {
            Z = color(1, 2);
            A = 'H';
        }
        return;
    }
    int last = state(S[0]) == 2 ? dir(S[0]) : stackdir;
    for (int i = 0; i < 4; i++)
    {
        int d = (last + i) % 4 + 1;
        if (d == stackdir)
        {
            Z = color(stackdir, 1);
            A = move[stackdir];
            return;
        }
        if (S[d] == 0 || (state(S[d]) == 1 && dir(S[d]) == opp[d]))
        {
            Z = color(d, 2);
            A = move[d];
            return;
        }
    }
}

