bool solve(int a,int b){
    if(vis[a][b])return dp[a][b];
    vis[a][b]=1;
    if(a==m&&b==n)return dp[a][b]=1;
    if(s1[a]==tmp[a+b]&&a<m){
        dp[a][b]|=solve(a+1,b);
    }
    if(s2[b]==tmp[a+b]&&b<n){
        dp[a][b]|=solve(a,b+1);
    }
    dp[a][b]|=0;
    return dp[a][b];
}
