        return w>o.w;
    }
};
int32_t main(){
    cin.tie(nullptr)->sync_with_stdio(false);
    int n,st,en,m;cin>>n>>st>>en>>m;    
    vector<vector<pii>> adj(n);
    while(m--){
        int u,v,w;cin>>u>>v>>w;
        adj[u].push_back({v,w});
    }
    vector<vector<int>> dist(n,vector<int>(n,LLONG_MAX));
    vector<pii> path;
    priority_queue<A> dijk;
    dist[st][0]=0,dijk.push({st,0,-1});
    while(dijk.size()){
        A x=dijk.top();
        int u=x.u,w=x.w,t=x.t;
        dijk.pop();
        if(dist[u][t]<w)continue;
        if(u==en){
            path.push_back({w,t});
            continue;
        }
        for(pii v:adj[u]){
            if(t+1<n&&dist[v.f][t+1]>w+v.s){
                dist[v.f][t+1]=w+v.s;
                dijk.push({v.f,w+v.s,t+1});
            }
        }
    }
    int L;cin>>L;
    while(L--){
        int h,ans=LLONG_MAX;cin>>h;
        for(pii x:path)ans=min(ans,x.f+x.s*h);
        cout<<ans<<' ';
    }
    return 0;
}

