inline int f(int l, int r, int t) {
  return p2[t]-p2[l-1] + s1[t+1] - s1[r+1];
}
void solve() { 
  cin >> n;
  for (int i = 1; i <= n; ++i) {
    cin >> a[i];
    p1[i] = p1[i-1];
    p2[i] = p2[i-1];
