LL n,m,k,p;
LL S[50001];
LL f(LL t)
{
    if(!t)return 0%p;
    if(t==1)return 1%p;
    LL s1,s2;
    if(Map[t-1])s1=Map[t-1];
    else s1=S[(t-1)%n];
    if(Map[t-2])s2=Map[t-2];
    else s2=S[(t-2)%n];
    return (s1*f(t-1)+s2*f(t-2))%p;
}
