int mountain[21][21];
int Max = -10000;


void check(int x, int y, int r, int c) {
    if(mountain[x][y] > Max) Max = mountain[x][y];
    if(x > 0 && mountain[x-1][y] > mountain[x][y] && mountain[x-1][y] != 100) {
        check(x-1, y, r, c);
    }
    if(x < r-1 && mountain[x+1][y] > mountain[x][y] && mountain[x+1][y] != 100) {
        check(x+1, y, r, c);
    } 
    if(y > 0 && mountain[x][y-1] > mountain[x][y] && mountain[x][y-1] != 100) {
        check(x, y-1, r, c);
    }
    if(y < c-1 && mountain[x][y+1] > mountain[x][y] && mountain[x][y+1] != 100) {
        check(x, y+1, r, c);
    }
}


