int find(int l,int r,int k)
{
 while (l<r)
 {
  int mid=(l+r)/2;
  if (b[mid]<k)r=mid;
  else l=mid+1;
 }
 return l;
}
void calc(int l,int r)
{
 memset(b,0,sizeof(b));
 f[l]=k=1;
 b[1]=a[l];
 for (int i=l+1;i<=r;i++)
 {
  t=find(1,k+1,a[i]);
  k=max(k,t);
  b[t]=a[i];
  f[i]=k;
 }
}
