 if (cnt[v[pos]]>0&&cnt[v[pos]]<MC) --hv[cnt[v[pos]]];
 ++cnt[v[pos]];
 if (cnt[v[pos]]>0&&cnt[v[pos]]<MC) ++hv[cnt[v[pos]]];
}
inline void del(int pos){
 if (cnt[v[pos]]>0&&cnt[v[pos]]<MC) --hv[cnt[v[pos]]];
 --cnt[v[pos]];
 if (cnt[v[pos]]>0&&cnt[v[pos]]<MC) ++hv[cnt[v[pos]]];
}
inline void mod(int pos,int x,bool inrange){
 if (inrange) del(pos);
 v[pos]=x;if (inrange) add(pos);
}
