pair<int, int> operator+(pair<int, int> l, pair<int, int> r) {
 return mp(l.f + r.f, l.s + r.s);
}





void bfs(vector<pair<int, int> > front, int out[1000][1000]) {

 fill(explr[0], explr[0] + 1000*1000, false);

 for(int i=0;i<front.size();i++)
  explr[front[i].f][front[i].s] = true;


 vector<int> cost;
 cost.resize(front.size(), 0);



 //do the processing
 for(int i=0;i<front.size();i++) {

  out[front[i].f][front[i].s] = cost[i];

  for(auto mv : mov) {
   auto next = front[i] + mv;

   if(next.f >= 0 && next.f < n && next.s >= 0 && next.s < m && pass[next.f][next.s] && !explr[next.f][next.s]) {
    front.push_back(next);
    cost.push_back(cost[i] + 1);

    explr[next.f][next.s] = true;
   }
  }

 }


}







