using namespace std;

typedef long long ll;
typedef vector<int> vi;
typedef pair<int, bool> ii;
typedef vector<ii> vii;
const int MAXN = 1e7;
const int mod = 1e9 + 7;
const int Hash = 1007050321;

vi P, rem; vector<bool> nt;
vi F2, FF;
vector<vi> dv;
int n;
vi A;
vector<vii> sets;
map<ii, int> M;

void sieve() {
    init(rem, MAXN + 1, 0);
    init(sets, n, vii()); init(dv, n, vi());
    init(nt, MAXN + 1, 1); nt[0] = nt[1] = 0;
    poi(i, 1, MAXN + 1) if (nt[i]) {
        P.pb(i); rem[i] = (int)P.size() - 1;
        for (ll j = 1LL * i * i; j <= MAXN; j += i) {
            nt[j] = 0; rem[j] = (int)P.size() - 1;
        }
    }
    init(F2, P.size(), 1); poi(i, 1, P.size()) F2[i] = int(F2[i - 1] * 2LL % Hash);
    init(FF, n + 1, 1); poi(i, 1, n + 1) FF[i] = FF[i - 1] * 2LL % mod;
}

int ans = 0;
int total = 0;
vi F;

void gen(int cur, int f, int num, int type) {
    if (cur == (int)dv[num].size()) { if (f) sets[num].pb(ii(f, type)); return; }
    gen(cur + 1, f, num, type); gen(cur + 1, (f + F2[dv[num][cur]]) % Hash, num, type ^ 1);
}

void prepare_sets() {
    init(F, n, 0);
    poi(i, 0, n) {
        int cur = A[i]; while (cur > 1) { dv[i].pb(rem[cur]); while (P[rem[cur]] != cur && rem[cur] == rem[rem[cur]]) cur /= P[rem[cur]]; cur /= P[rem[cur]]; }
        gen(0, 0, i, 0);    
        poi(j, 0, sets[i].size()) ++M[sets[i][j]];
    }
    for (map<ii, int>::iterator it = M.begin(); it != M.end(); ++it) total = (total + (it->ft.nd ? 1 : -1) * (FF[it->nd] - 1) % mod + mod) % mod;
    poi(i, 0, n) {
        poi(j, 0, sets[i].size()) F[i] = (F[i] + (sets[i][j].nd ? 1 : -1) * (FF[M[sets[i][j]]] - 1) % mod + mod) % mod;
    }
}

void calculate_one() { 
    poi(i, 0, n) ans = (ans + total - F[i]) % mod;
}

int main() {
    ios_base::sync_with_stdio(false); cin.tie(0);
    cin >> n;
    init(A, n, 0); poi(i, 0, n) cin >> A[i];
    sieve(); 
    prepare_sets(); calculate_one(); 
    cout << ans << endl;
}

