struct Axis {
    int left;
    int right;
};


std::vector<Axis> axes;


int minSwaps(int axis) {
    // Base case: No swaps needed for an empty axis.
    if (axis == -1) {
        return 0;
    }


    Axis current = axes[axis];


    int left_swaps = minSwaps(current.left);
    int right_swaps = minSwaps(current.right);


    // Check for impossible conditions.
    if (left_swaps == -1 || right_swaps == -1 || abs(left_swaps - right_swaps) > 1) {
        return -1;
    }


    return left_swaps + right_swaps + (left_swaps != right_swaps);
}


int main() {
    int n;
    std::cin >> n;
    axes.resize(n + 1); // 1-based indexing


    for (int i = 1; i <= n; i++) {
        std::cin >> axes[i].left >> axes[i].right;
    }


    int result = minSwaps(1); // Start from the top axis (axis 1)
    std::cout << result << std::endl;


    return 0;
}

