                vis[u][1 - v.y] = 1;
                dp[u][1 - v.y] = v.x;
                Q.push(pii(u, 1 - v.y));
            }
        }
    }
}

void Cycle (int v)
{
    if (inStack[v]) { draw = 1; return; }

    inStack[v] = 1;
    for (auto u : g[v]) Cycle (u);
    inStack[v] = 0;
}

