void st()
{
    int all=2*n-1;
    for(int i=0; i<all; i++)
        dp[i][0]=i;
    for(int j=1; (1<<j)<=all; j++)
    {
        for(int i=0; i+(1<<j)-1<all; i++)
        {
            int l=dp[i][j-1],r=dp[i+(1<<(j-1))][j-1];
            dp[i][j]=deep[l]<deep[r]?l:r;
        }
    }
}
int rmq(int l,int r)
{
    int m=first[l],n=first[r];
    if(m>n)swap(m,n);
    int k=log(n-m+1)/log(2);
    int ll=dp[m][k],rr=dp[n-(1<<k)+1][k];
    return ver[deep[ll]<deep[rr]?ll:rr];
}
void dfs(int value,int now)
{
    ver[++cnt]=value,deep[cnt]=now,first[value]=cnt,Deep[value]=now;
    vis[value]=true;
    for(int i=0; i<object[value].size(); i++)
    {
        if(!vis[object[value][i]])
        {
            dfs(object[value][i],now+1);
            ver[++cnt]=value;
            deep[cnt]=now;
        }
    }
}
void findpath()
{
    memset(vis,0,sizeof(vis));
    mleft=bfs(1);
    memset(vis,0,sizeof(vis));
    mright=bfs(mleft);
}
int bfs(int u)
{
    queue<int> last;
    int now;
    last.push(u);
    vis[u]=true;
    while(!last.empty())
    {
        now=last.front();
        last.pop();
        for(int i=0;i<object[now].size();i++)
        {
            int v=object[now][i];
            if(!vis[v])
            {
                last.push(v);
                vis[v]=true;
            }
        }
    }
    return now;
}
void findfather(int u,int fa,bool judge)
{
    if(u==fa)
    return;
    online[u]=true;
    if(judge)
        dark.push(u);
    else
    path.push_back(u);
    for(int i=0;i<object[u].size();i++)
    {
        int v=object[u][i];
        if(Deep[v]<Deep[u])
        {
            findfather(v,fa,judge);
            return;
        }
    }
}

