#include <algorithm>
#include <iostream>
#include <vector>

int main() {
    int n;
    std::cin >> n;
    std::vector<int> x(n);
    for (int i = 0; i != n; ++i) {
        std::cin >> x[i];
    }
    int minimum = 1e5, maximum = -1e5;
    for (int i = 0; i != n; ++i) {
        minimum = std::min(minimum, x[i]);
        maximum = std::max(maximum, x[i]);
    }
    if (maximum - minimum < 2) {
        for (int i = 0; i != n; ++i) {
            std::cout << x[i] << ' ';
        }
        std::cout << '\n';
    } else {
        for (int i = 0; i != n; ++i) {
            x[i] -= (minimum + 1);
        }
        int lower = 0, zero = 0, upper = 0;
        for (int i = 0; i != n; ++i) {
            if (x[i] < 0) {
                ++lower;
            } else if (x[i] == 0) {
                ++zero;
            } else {
                ++upper;
            }
        }
        int a = std::min(lower, upper) * 2, b = zero / 2 * 2;
        if (a > b) {
            std::cout << n - a << '\n';
            for (int i = 0; i != n; ++i) {
                if (a && x[i] == -1) {
                    x[i] = 0;
                    --a;
                } else if (a && x[i] == 1) {
                    x[i] = 0;
                    --a;
                }
            }
        } else {
            std::cout << n - b << '\n';
            int c = b / 2, d = b / 2;
            for (int i = 0; i != n; ++i) {
                if (c && x[i] == 0) {
                    x[i] = -1;
                    --c;
                } else if (d && x[i] == 0) {
                    x[i] = 1;
                    --d;
                }
            }
        }
        for (int i = 0; i != n; ++i) {
            std::cout << x[i] + minimum + 1 << ' ';
        }
        std::cout << '\n';
    }
}

