int MaxSubarray(int arr[], int n){
    int best = 0, sum = 0;
    for(int i=0; i<n; ++i){
        sum = max( arr[i], arr[i] + sum );
        best = max( best, sum );
    }
    return best;
}

long long GCD(long long a, long long b){
    return b ? GCD(b, a%b) : a ;
}
long long LCM(long long a, long long b){
    return a / GCD(a, b) * b;
}

long long fast_power(long long b, long long p){
    long long res=1;
    while(p){
        if(p%2) res = ( res * b ) % mod;
        b = ( b * b ) % mod;
        p /= 2;
    }
    return res;
}

long long maxSum(long long arr[],int sz){
    long long best = -1e18 , sum = 0 ;
    for(int i=0;i<sz;++i){
        sum = max( arr[i] , arr[i]+sum );
        best = max( best , sum );
    }
    return best ;
}
