struct Chem {
	int id;
	int props[15];
};


int N,P;
Chem chems[100005];
int diffs[15] = {0};


int compare(const void *a, const void *b) {
	const Chem *chemA = (const Chem *)a;
	const Chem *chemB = (const Chem *)b;
	for(int i=0; i<P; i++) {
		if(chemA->props[i] == chemB->props[i])
			continue;
		return chemA->props[i] - chemB->props[i];
	}
	return 0;
}


int compareProp(Chem a, int targets[]) {
	int match = 0;
	for(int i=0; i<P; i++) {
		if(a.props[i] < targets[i])
			return -1;
		if(a.props[i] > targets[i])
			return 1;
	}
	return 0;
}


int binary_search(int left, int right) {
	while(left <= right) {
		int mid = left + (right-left)/2;
		int cmp = compareProp(chems[mid], diffs);
		if(cmp == 0)
			return chems[mid].id;
		if(cmp == -1)
			left = mid+1;
		else if(cmp == 1)
			right = mid-1;
	}
	return -1;
}


