const int INF = 1e9;

char s[100005];
int n, lcnt = 0, rcnt = 0;
vector<int> lans, rans;
int lbest = INF, rbest = INF;
set<int> l, r;

void solve(vector<int> &ans, int &best) {
    REP(i, n) if (s[i] == 'L') l.insert(i);
    else r.insert(i);
    best = 0;
    int pos = -1;
    REP(i, n) {
        if (i & 1) {
            auto it = r.lower_bound(pos);
            if (it == r.end()) {
                ++best;
                pos = *r.begin();
                ans.pb(pos);
                r.erase(r.begin());
            } else {
                pos = *it;
                ans.pb(pos);
                r.erase(it);
            }
        } else {
            auto it = l.lower_bound(pos);
            if (it == l.end()) {
                ++best;
                pos = *l.begin();
                ans.pb(pos);
                l.erase(l.begin());
            } else {
                pos = *it;
                ans.pb(pos);
                l.erase(it);
            }
        }
    }
}

int main() {
    scanf("%s", s);
    n = strlen(s);
    REP(i, n) if (s[i] == 'L') ++lcnt;
    else ++rcnt;
    if (lcnt >= rcnt) solve(lans, lbest);
    REP(i, n) s[i] = (s[i] == 'L') ? 'R' : 'L';
    if (rcnt >= lcnt) solve(rans, rbest);
    if (lbest < rbest) {
        printf("%d\n", lbest);
        REP(i, n) printf("%d ", lans[i] + 1);
    } else {
        printf("%d\n", rbest);
        REP(i, n) printf("%d ", rans[i] + 1);
    }
    printf("\n");
    return 0;
}

