const int MAXN = 300008;

int a[MAXN];

void solve() {
    string s;
    cin >> s;
    int n = s.length();
    vector<int> c(10, 0);
    for (int i = 0; i < n; ++i) {
        c[a[i] = (s[i] - '0')] ^= 1;
    }

    for (int i = n - 1; i >= 0; --i) {
        int k = n - i;
        c[a[i]] ^= 1;

        if (a[i] == 0) {
            continue;
        }

        int cn = 0;
        for (int x : c) {
            cn += x;
        }

        if (cn > k) {
            continue;
        }

        if (cn == k) {
            int p = 0;
            while (!c[p]) {
                ++p;
            }

            if (p >= a[i]) {
                continue;
            }
        }

        vector<int> digs;
        for (int j = 0; j < 10; ++j) {
            if (c[j]) {
                digs.push_back(j);
            }
        }

        if (digs.size() != k) {
            digs.push_back(a[i] - 1);
            digs.push_back(a[i] - 1);
        }

        while (digs.size() < k) {
            digs.push_back(9);
            digs.push_back(9);
        }

        sort(digs.begin(), digs.end(), greater<int>{});

        int p = 0;
        while (p < digs.size() && digs[p] >= a[i]) {
            ++p;
        }

        assert(p != digs.size());

        cout << s.substr(0, i);

        if (i == 0 && digs[p] == 0) {
            digs.resize(digs.size() - 2);
        } else {
            cout << digs[p];
            swap(digs[p], digs.back());
            digs.resize(digs.size() - 1);
        }

        for (int x : digs) {
            cout << x;
        }

        cout << "\n";

        return;
    }

    assert(false);
}

