typedef long long ii;


bool visited[16]={0};
ii cord[11][3]={0};
ii comps[11][3]={0};


ii dist(int i,int j){
    return (cord[i][0]-cord[j][0])*(cord[i][0]-cord[j][0])+(cord[i][1]-cord[j][1])*(cord[i][1]-cord[j][1])+(cord[i][2]-cord[j][2])*(cord[i][2]-cord[j][2]);
}


ii n,m;


//Shortest path to get <n,n,n> from now.
ii solve(ii now,ii a,ii b,ii c){
    if(a>=n&&b>=n&&c>=n)return 0;
    ii mn = INT_MAX;
    visited[now]=1;
    for(int i=1;i<=m;i++){
        if(visited[i])continue;
        mn = min(mn,solve(i,a+comps[i][0],b+comps[i][1],c+comps[i][2]) + dist(now,i));
    }
    visited[now]=0;
    return mn;
}


