ll bp(ll a, ll b)
{
 {
  if (b == 0)
  {
   return 1;
  }
  else
   if (b % 2 == 0)
   {
   ll aq = bp(a, b / 2);
   return (aq*aq) % p;
   }
   else
   {
    ll aq = bp(a, b - 1);
    return (aq*a) % p;
   }
 }
}
