{
 nt x = v1.size(), y = v2.size();
 v3.resize(x + y);
 for (nt j = 0; j < x; j++)
 {
  for (nt h = 0; h < y; h++)
  {
   v3[j + h] += v1[j] * v2[h];
   v3[j + h] %= p;
  }
 }
