using namespace std;

typedef long long ll;
typedef long double ld;

ll k, d, t;

bool check(ld q) {
  ll blocks = (ll)q / d;
  ll off_in_block = d - k;
  ld on_in_block = k;
  ld cook = blocks * (on_in_block * ((ld)1 / t) + off_in_block * ((ld)1 / (t * 2)));
  ld rem = q - blocks * d;
  cook += min((ld)k, rem) * ((ld)1 / t) + max((ld)0, rem - k) * ((ld)1 / (t * 2));
  return cook >= 1;
}

int main() {
  //freopen("input.txt", "r", stdin);
  //freopen("output.txt", "w", stdout);
  cin >> k >> d >> t;
  if (k > d) {
    d = ((k + d - 1) / d) * d;
  }
  ld low = 0.0;
  ld high = 3e18;
  for (int iter = 0; iter < 16384; iter++) {
    ld mid = (low + high) * 0.5;
    if (check(mid)) {
      high = mid;
    } else {
      low = mid;
    }
  }
  cout.precision(20);
  cout << fixed << 0.5 * (low + high) << "\n";
  return 0;
}

