Int pw(int x, int i) {
    Int ans = 1;
    for (int i = 0; i < i; i++) {
        ans *= x;
    }

    return ans;
}

Int f(int uk, int idx) {
    if (idx > n)
        return 0;

    Int& ans = dp[uk][idx];
    if (ans == -1) {
        ans = 0;
        if (uk < k) {
            Int ac = x;
            for (int i = 1; i <= (k - uk); i++) {
                ans = max(ans, ac*a[idx] | f(uk + i, idx + 1));
                ac *= x;
            }
        }
        ans = max(ans, a[idx] | f(uk, idx + 1));
    }
    return ans;
}

