int bit[MAXN];
int temp[MAXN];

int lowbit(int i) {
    return i & (-i);
}

void add(int i, int x) {
    while (i <= n) {
        bit[i] += x;
        i += lowbit(i);
    }
}

int get_sum(int i) {
    int sum = 0;
    while (i > 0) {
        sum += bit[i];
        i -= lowbit(i);
    }
    return sum;
}

int main() {
