            temp[j] = arr[j];
        }
        int cnt = 0;
        for (int j = l; j <= r; ++j) {
            arr[j] = temp[r - cnt];
            cnt++;
        }
        for (int j = 1; j <= n; ++j) {
            add(arr[j], 1);
            sum += j - get_sum(arr[j]);
