#include<bits/stdc++.h>
using namespace std;

#define MX 205
vector<int>v[MX];
queue<int>q;
bool vis[MX];
int level[MX];
int n,k;

void BFS()
{
    int mn = 0;
    while(!q.empty())
    {
        int x = q.front();
        q.pop();
        vis[x] = true;
        mn = max(mn,level[x]);
        for(int i=0; i<(int)v[x].size(); i++)
        {
            if(!vis[v[x][i]])
            {
                vis[v[x][i]] = true;
                level[v[x][i]] = level[x]+1;
                q.push(v[x][i]);
            }
        }
    }
    printf("%d\n",mn);
    return;
}

void clr()
{
    for(int i=0; i<MX; i++) v[i].clear();
    memset(vis,false,sizeof(vis));
    memset(level,0,sizeof(level));
    return;
}

int main()
{
    int t,i,x;
    scanf("%d",&t);
    while(t--)
    {
        clr();
        scanf("%d %d",&n,&k);
        for(i=1; i<=n; i++)
        {
            if(i==1)    v[i].push_back(i+1);
            else if(i==n)   v[i].push_back(i-1);
            else
            {
                v[i].push_back(i+1);
                v[i].push_back(i-1);
            }
        }
        for(i=0; i<k; i++)
        {
            scanf("%d",&x);
            q.push(x);
            vis[x] = true;
            level[x] = 1;
        }
        if(n==1)
        {
            puts("1");
            continue;
        }
        BFS();
    }
    return  0;
}

