  int n, a[MAXN], ans, mn = MAXN, h[3] = {};
  cin >> n;
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
    mn = min(mn, a[i]);
  }
  for (int i = 0; i < n; ++i) {
    h[a[i]-mn]++;
  }
  if (min(h[0], h[2])*2 < h[1]/2*2) {
    ans = n-h[1]/2*2;
    h[0] += h[1]/2;
    h[2] += h[1]/2;
    h[1] = h[1]%2;
  } else {
    int dec = min(h[0], h[2]);
    ans = n-dec*2;
    h[0] -= dec;
    h[2] -= dec;
    h[1] += 2*dec;
  }
  cout << ans << endl;
  for (int i = 0; i < 3; ++i)
    for (int j = 0; j < h[i]; ++j)
      cout << mn+i << " ";
  cout << endl;
