}

#include<bits/stdc++.h>
using namespace std;

#define MAX 500001

int gcd(int a,int b)
{
    if(a<b){
        int t = a;
        a = b;
        b = t;
    }

    if(a%b==0)
        return b;
    return gcd(b,a%b);
}

int ar[MAX],tree[4*MAX+1];

void init(int n,int s,int e)
{
    if(s==e)
    {
        tree[n] = ar[s];
        return;
    }

    int r = 2*n;
    int l = 2*n+1,mid = (s+e)/2;

    init(r,s,mid);
    init(l,mid+1,e);
    tree[n] = gcd(tree[r],tree[l]);
    return;
}

int query(int n,int l,int r,int s,int e,int x)
{
    //cout<<n<<" "<<l<<" "<<r<<" "<<s<<" "<<e<<" "<<x<<endl;
    if(s>r || e<l)
        return -1;
    else if(s<=l && e>=r)
    {
        //cout<<"here"<<endl;
        if(tree[n]%x==0 || l==r)
            return tree[n];
    }

    int lt = query(2*n,l,(l+r)/2,s,e,x);
    if(lt==0)
        return 0;
    int rt = query(2*n+1,(l+r)/2+1,r,s,e,x);
    if(rt==0)
        return 0;

    if(lt==-1)
        return rt;
    else if(rt==-1)
        return lt;
    else{
        if(rt%x!=0 && lt%x!=0)
            return 0;
        else
            return gcd(rt,lt);
    }
}

void update(int n,int l,int r,int i,int nwval)
{
    //cout<<n<<" "<<l<<" "<<r<<" "<<i<<" "<<nwval<<endl;
    if(l>i || r<i)
        return;
    else if(l==r){
        tree[n] = nwval;
        return;
    }
    int mid = (l+r)/2;
    update(2*n,l,mid,i,nwval);
    update(2*n+1,mid+1,r,i,nwval);
    tree[n] = gcd(tree[2*n],tree[2*n+1]);
    return;
}

