ll mcm(int l, int r) {
    if (l == r) return 0;
    if (dp[l][r] != -1) return dp[l][r];
    ll mx = 0;
    for (int k = l; k < r; k++) {
        ll score1 = mcm(l,k), score2 = mcm(k+1,r);
        ll sum1 = qs[k]-qs[l-1], sum2 = qs[r]-qs[k];
        if (sum1 > sum2) swap(sum1,sum2);
        sum1 *= 2;
        mx = max(mx,score1+score2+sum1+sum2);
        //cout << l << " " << r << " " << k << " : " << sum1 << " " << sum2 << "\n";
    }
    //cout << l << " " << r << " " << mx << "\n";
    return dp[l][r] = mx;
}


