#include<bits/stdc++.h>
using namespace std;
int N, M, P, A, B;
vector<int> graph[200010];
pair<int, int> stage[200010]; // pos A, B {a, b}
int check_A[200010], check_B[200010];
priority_queue<pair<int, int>> pq;
void bfs_b(int k)
{
    queue<pair<int, int>> qu; // BFS -> B
    qu.push({B, 0});
    while(!qu.empty())
    {
        auto cur = qu.front();
        qu.pop();
        if(check_B[cur.first]) continue;


        stage[cur.first].second = cur.second;
        check_B[cur.first] = 1;
        for(auto j : graph[cur.first])
        {
            if(check_B[j])continue;
            // cout << j << ' ';
            qu.push({j, cur.second + 1});
        }
    }
}


void bfs_a()
{
    int dis;
    queue<pair<int, int>> qu; // BFS -> A
    qu.push({A, 0});
    while(!qu.empty())
    {
        auto cur = qu.front();
        qu.pop();
        if(check_A[cur.first]) continue;
        check_A[cur.first] = 1;


        stage[cur.first].first = cur.second;
        for(auto j : graph[cur.first])
        {
            if(check_A[j])continue;
            // cout << j << ' ';
            qu.push({j, cur.second + 1});
        }
    }
}




int main()
{
    cin.tie(0)->ios::sync_with_stdio(0);
    cin >> N >> M >> P >> A >> B;
    for(int i = 0, u, v; i < M; i++)
    {
        cin >> u >> v;
        graph[v].push_back(u);
        graph[u].push_back(v);
    }


    bfs_b(N);
    bfs_a();
    for(int i = 1; i <= N; i++)
    {
        pq.push({stage[i].first, i});// {position A, node}
    }
    int ans[200007];
    for(int i = 0; i < N; i++)
    {
        while(!pq.empty() && stage[pq.top().second].second <= i) pq.pop();
        ans[i] = pq.top().first;
    }


    for(int i = 0, Xi; i < P; i++)
    {
        cin >> Xi;
        cout << ans[Xi] << '\n';
    }
}
