bool isable(long long p){
 if(p>n)return false;
 long long count;long long sum;long long loop;
 sum=0;count=0;loop=0;
 while(count<p){
  if(a[id[loop]]<p){loop++;continue;}
  sum=sum+t[id[loop]];
  count++;
  if(sum>time)return false;
  if(count==p)break;
  loop++;
  if(loop>=n)return false;
 }
 return true;
}
void print(long long p){
