 while(count<p){
  if(a[id[loop]]<p){
   loop++;
   continue;
  }
  count++;
  printf("%d ",id[loop]+1);
  if(sum>time)return;
  if(count==p)return;
  loop++;
  if(loop>=n)return;
 }
}
