void tim(int x,int y)
{
    if(x==0||y==0)
    {
        cout<<x<<" "<<y<<endl;;
    }
    else timm(x,y);
}
void timm(int x,int y)
{
    if(x>=2*y)
    {x=x-2*y;tim(x,y);}
    else timmm(x,y);
}
void timmm(int x,int y)
{
    if(y>=2*x)
    {
        y=y-2*x;
        tim(x,y);
    }
    else {cout<<x<<" "<<y<<endl;;}
}
