}
ll lcm(ll a, ll b)
{
    return a / gcd(a, b) * b;
}
ll binary_power(ll a, ll n)
{
    ll res = 1;
    while(n > 0)
    {
        if(n % 2 == 0)
        {
            a *= a;
            n >>= 1;
        }
        else
        {
            res *= a;
            n--;
        }
    }
    return res;
}
int bitCount(ll n)
{
