int LowerBound(int L, int R, int x, ll w[])
{
    // L = 0, R = n
    while(R - L > 1)
    {
        int mid = (L + R) >> 1;
        if(w[mid] >= x)
            R = mid;
        else
            L = mid;
    }
    if(w[R] == x)
        return R;
    return -1;
}
int UpperBound(int L, int R, int x, ll w[])
{
    // L = 1, R = n + 1
    while(R - L > 1)
    {
        int mid = (L + R) >> 1;
        if(w[mid] <= x)
            L = mid;
        else
            R = mid;
    }
    if(w[L] == x)
        return L;
    return -1;
}
