mat tree[N << 1];

mat tget(int l,int r) {
  mat resl(1), resr(1);
  for (l += N, r += N; l <= r; l >>= 1, r >>= 1) {
    if (l & 1) {
      resl = resl * tree[l];
      l++;
    }
    if (~r & 1) {
      resr = tree[r] * resr;
      r--;
    }
  }
  return resl * resr;
}

