const ld PI = 2 * acos(0);

ld x[2], y[2];
ld v, t;
ld vx, vy, wx, wy;

ld calc(ld a, ld b, ld c) {
 ld delta = b * b - 4 * a * c;
 if (delta < 0) return INF;
 ld r1 = (-b + sqrt(delta)) / 2 / a;
 ld r2 = (-b - sqrt(delta)) / 2 / a;
 if (r2 >= 0) return r2;
 if (r1 >= 0) return r1;
 return INF;
}

void solve() {
 FOR(i, 0, 2) cin>>x[i]>>y[i];
 cin>>v>>t>>vx>>vy>>wx>>wy;
