 ld ans = calc(vx * vx + vy * vy - v * v, -2 * (a * vx + b * vy), a * a + b * b);
 if (ans > t) ans = INF;
 a = x[1] - x[0] - t * vx;
 b = y[1] - y[0] - t * vy;
 ans = min(ans, t + calc(wx * wx + wy * wy - v * v, -2 * (a * wx + b * wy + v * v * t), a * a + b * b - v * v * t * t));
 cout<<fixed<<setprecision(6)<<ans;
}

