#include <bits/stdc++.h>
typedef long long ll;
using namespace std;
const ll Nx = 105;
const ll inf = 1e18;


ll n, m, u, v, w, s, d, f, fuelc[Nx];
vector<pair<ll,ll>> g[Nx];
vector<vector<vector<ll>>> dt(Nx, vector<vector<ll>>(101, vector<ll>(2, inf)));


void dijkstra() {
    priority_queue<pair<pair<ll,ll>,pair<ll,ll>>, vector<pair<pair<ll,ll>,pair<ll,ll>>>, greater<pair<pair<ll,ll>,pair<ll,ll>>>> pq;
    dt[s][0][0] = 0;
    pq.push({{0, s}, {0, 0}});
    while(!pq.empty()) {
        auto [val, node] = pq.top().first;
        auto [fuel, free] = pq.top().second;
        pq.pop();
        if (val > dt[node][fuel][free]) continue;
        if (fuel+1 <= f) {
            if (!free && val < dt[node][f][1]) {
                dt[node][f][1] = val;
                pq.push({{val, node}, {f, 1}});
            }
            if (val+fuelc[node] < dt[node][fuel+1][free]) {
                dt[node][fuel+1][free] = val+fuelc[node];
                pq.push({{dt[node][fuel+1][free], node}, {fuel+1, free}});
            }
        }
        for (auto [usefuel, nnode] : g[node]) {
            if (fuel >= usefuel) {
                if (val < dt[nnode][fuel-usefuel][free]) {
                    dt[nnode][fuel-usefuel][free] = val;
                    pq.push({{val, nnode}, {fuel-usefuel, free}});
                }
            }
        }
    }
}


